import React, { useState } from "react";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import PropTypes from "prop-types";
// import {login} from "actions/auth";
import { Button, Form, Input } from "reactstrap";
import "./index.scss";
import Alert from "views/Notifications/Alert";
import logo from "assets/logo/logo.png";
import * as Constants from "constants/index";

const Login = ({ errorList, loading, history }) => {
  // const [formData, setFormData] = useState({ email: "", password: "" });
  // const { email, password } = formData;
  // const onChange = (e) => {
  //   setFormData({
  //     ...formData,
  //     [e.target.name]: e.target.value.split(" ").join(""),
  //   });
  // };
  const onSubmit = (e) => {
    e.preventDefault();
  };

  return (
    <div className="step">
      <div className="step__logo">
        <img className="step__img" src={logo} />
        <h3 className="heading-3">Eden</h3>
      </div>
      <div className="step__num"></div>
      <div className="step__paragraph">
        <h2 className="heading-2">{Constants.WELCOME_FIRST}</h2>
        <p className="step__para">{Constants.CHANGE_LATER}</p>
      </div>
      <Form onSubmit={(e) => onSubmit(e)}>
        <div className="step__content">
          <div className="form__group">

          <label htmlFor="name" className="form__label" aria-hidden="true">
              Full Name
            </label>
            <Input
              className="form__input"
              type="text"
              name="name"
              autoFocus
              maxLength="50"
              minLength="5"
              aria-describedby="Steve Jobs"
              placeholder="Steve Jobs"
              // value={name}
              // onChange={(e) => onChange(e)}
              // invalid={errorList.name ? true : false}
              // required
            />
            <span
              // className={`login__err form__label--${
              //   isValid ? "white" : "error"
              // }`}
              role="alert"
              aria-live="assertive"
            >
              {/* {message} */}Required
            </span>
          </div>
        </div>
      </Form>
      <div className="step__btns"></div>
    </div>
  );
};

Login.proTypes = {
  isAuthenticated: PropTypes.bool,
  errorList: PropTypes.object.isRequired,
  loading: PropTypes.bool.isRequired,
};

const mapStateToProps = (state) => ({
  isAuthenticated: state.auth.isAuthenticated,
  errorList: state.errors,
  loading: state.auth.loading,
});

export default connect(mapStateToProps, {})(Login);
